mapper.select:
select

from REQUEST_BLOOD_TRANSFUSION
where 1=1

  AND REQUEST_NO = #{requestNo,jdbcType=VARCHAR}


  AND  INPATIENT_NO = #{inpatientNo,jdbcType=VARCHAR}


  AND OUTPATIENT_NO = #{outpatientNo,jdbcType=VARCHAR}


  AND  OPEN_BILL_TIME >= #{openBillTimeLow,jdbcType=VARCHAR}


   AND  OPEN_BILL_TIME <= #{openBillTimeHigh,jdbcType=VARCHAR}


  AND OPEN_BILL_DOCTOR_CODE = #{openBillDoctorCode,jdbcType=VARCHAR}


  AND  IDENTITY_NO = #{identityNo,jdbcType=VARCHAR}


  AND PATIENT_ID = #{patientId,jdbcType=VARCHAR}
